<?php

namespace Runalyze\Calculation\Activity;

/**
 * Generated by PHPUnit_SkeletonGenerator 1.2.0 on 2015-01-21 at 09:17:29.
 */
class TimeArrayMinifierTest extends \PHPUnit_Framework_TestCase {

	public function testExtend()
    {
        $timeArray = [0, 1, 3, 5, 2, 1, 10];

        $this->assertEquals([
            0, 1, 4, 9, 11, 12, 22
        ], TimeArrayMinifier::extend($timeArray));
    }

	public function testShorten() {
        $timeArray = [0, 2, 5, 20, 50, 51];

        $this->assertEquals([
			0, 2, 3, 15, 30, 1
		], TimeArrayMinifier::shorten($timeArray));
	}

    public function testBackAndForward() {
        $timeArray = [0, 2, 5, 20, 50, 51];
        $timeMinified = TimeArrayMinifier::shorten($timeArray);
        $timeExtended = TimeArrayMinifier::extend($timeMinified);

        $this->assertEquals(
            $timeArray
        , $timeExtended);
    }




}
